/**
 * Minimal PDF.js worker for local use
 * This is a simplified version that handles basic operations
 */

// Register as a service worker
self.onmessage = function(event) {
  const data = event.data;
  
  console.log('PDF Worker received message', data.type);
  
  // Process message based on type
  switch (data.type) {
    case 'test':
      // Test message - just respond with ready
      self.postMessage({ type: 'test', result: true });
      break;
      
    default:
      // For any other message, respond with ready
      self.postMessage({ type: 'ready' });
  }
};

// Let the main thread know we're ready
self.postMessage({ type: 'workerReady' });

console.log('PDF Worker initialized and ready for use'); 